import React from "react";
import { createContext } from "react";
import { useState } from "react";
import { THEME_TYPE } from "../constants";

const themeMode=THEME_TYPE.LIGHT

const ThemeProvider = ({ children }) => {
  const [isDark,toggleDarkMode]=useState(false)
  const [theme,setTheme]=useState(themeMode)

  const handleTheme=()=>{
    if(themeMode === THEME_TYPE.DARK )
    setTheme(THEME_TYPE.DARK)
  }
  const handleDark=()=>{
     toggleDarkMode(old=>!old)
  }

  return <ThemeContext.Provider value={{theme,handleTheme}}>{children}</ThemeContext.Provider>;
};



const ThemeContext = createContext({themeMode});

export { ThemeContext }

export default ThemeProvider;
