import React, { useContext } from "react";
import { THEME_TYPE } from "../constants";
import {useTheme} from "../useTheme"
import { ThemeContext } from "../providers/ThemeProvider"
import { useState } from "react";



const ThemeSwitcher = () => {
  // No need to change *return* part
  // You have to set themeMode based on context
  const [themeMode,setThemeMode] = useState(THEME_TYPE.LIGHT)
  const {isDark,handleDark}=useContext(ThemeContext)
 // const [themeMode,setThemeMode]=useState(themeMode)


  const handleThemeChange = (e) => {
     handleDark()
     console.log(isDark)

     if(isDark)
     {
      setThemeMode(THEME_TYPE.LIGHT);
     }
     if(!isDark)
     {
      setThemeMode(THEME_TYPE.DARK);
     }
  }

  return (
    <div className="switch-container">
      <label className="switch">
        <input
          data-testid="theme-changer"
          type="checkbox"
          checked={themeMode === THEME_TYPE.DARK}
          onChange={handleThemeChange}
        />
        <span className="slider round"></span>
      </label>
      <span className="text-color bold">Dark mode</span>
    </div>
  );
};

export default ThemeSwitcher;
