import React from "react";
import { createContext } from "react";
import { useState } from "react";
import { THEME_TYPE } from "../constants";

const themeMode=THEME_TYPE.LIGHT

const ThemeProvider = ({ children }) => {
  const [isDark,setDarkMode]=useState(false)
  const [theme,setTheme]=useState(themeMode)

  const handleTheme=()=>{

    if(themeMode === THEME_TYPE.LIGHT )
    {
      setTheme(THEME_TYPE.DARK)
    }
    if(themeMode === THEME_TYPE.DARK )
    {
      setTheme(THEME_TYPE.LIGHT)
    }
  }
  const handleDark=()=>{
     setDarkMode(!isDark)
  }

  return <ThemeContext.Provider value={{isDark,handleDark,theme,handleTheme}}>{children}</ThemeContext.Provider>;
};



const ThemeContext = createContext({themeMode});

export { ThemeContext }

export default ThemeProvider;
