import React from "react";
import { createContext } from "react";
import { useState } from "react/cjs/react.production.min";
import { THEME_TYPE } from "../constants";

const [isDark,toggleDarkMode]=useState(false)
const handleDark=()=>{
  toggleDarkMode(old=>!old)
}

const ThemeProvider = ({ children }) => {
  return <ThemeContext.Provider value={{isDark,}}>{children}</ThemeContext.Provider>;
};


const themeMode=THEME_TYPE.LIGHT
const ThemeContext = createContext({themeMode});

export { ThemeContext }

export default ThemeProvider;
